mapper.select:
SELECT COUNT(MEMBER_NO) FROM MEMBER WHERE
MEMBER_NAME LIKE '%' || #{keyword} || '%'
MEMBER_ID = #{keyword}
NICKNAME = #{keyword}
ORDER BY TOTAL_PRICE DESC

SELECT MEMBER_NO, MEMBER_ID, NICKNAME, MEMBER_NAME, ENROLLDATE, MEM_LEVEL, TOTAL_PRICE, MEMBER_POINT FROM MEMBER WHERE DELETE_YN = 'N'
AND MEMBER_NAME LIKE '%' || #{keyword} || '%'
AND MEMBER_ID = #{keyword}
AND NICKNAME = #{keyword}
ORDER BY TOTAL_PRICE DESC

